        [Function("EncodeToBase64")]
        public IActionResult Run([HttpTrigger(AuthorizationLevel.Function, "get", "post")] HttpRequest req)
        {
            _logger.LogInformation("C# HTTP trigger function processed a request.");


            string input = req.Query["input"];
            string base64String = "";

            if (string.IsNullOrWhiteSpace(input))
            {
                return new BadRequestObjectResult("Please pass a string in the 'input' query parameter.");
            }


            try
            {
                string Cliente = "Julio Cesar Noguera Pavon";
                string Cedula = "123456789";
                string Base64Firma = "";
                string ruta = @"C:\";

                var Item = new Procesar();

                var bytes = Item.GenerarBytesPDF_GIE(ruta, Cliente, Cedula, Base64Firma, Item.Lista());


                base64String = Convert.ToBase64String(bytes);
            }
            catch (Exception ex)
            {

                base64String = ex.Message;
            }




            return new OkObjectResult(new { original = input, base64String });
        }
    }


